mapper.sql:
select  
	from d_invoice_image


			and photo_id = #{photoId}


			and invoice_id = #{invoiceId}


			and file_name = #{fileName}


			and pos_longitude = #{posLongitude}


			and pos_latitude = #{posLatitude}


			and pos_height = #{posHeight}


			and photo_user_cd = #{photoUserCd}


			and photo_tm = #{photoTm}


			and upload_tm = #{uploadTm}


			and memo = #{memo}


			and check_tm = #{checkTm}


			and check_user_cd = #{checkUserCd}


			and status_cd = #{statusCd}

